mapper.select:
select *
  from tbl_reply
 where bno = #{bno}
 order by rno desc
 limit #{cri.pageStart}, #{cri.perPageNum}

select count(rno)
  from tbl_reply
 where bno = #{bno}

mapper.update:
update tbl_reply 
   set replytext = #{replytext}, updatedt = now()
 where rno = #{rno}

mapper.select:
select bno 
  from tbl_reply 
 where rno = #{rno}
